workflow-item.script:
var myNumber = 123;
var numberFormat;
var numberAsString;

numberFormat = "#";
numberAsString = System.formatNumber(myNumber, numberFormat);
System.log(numberFormat + ":  " + numberAsString);
numberFormat = "##";
numberAsString = System.formatNumber(myNumber, numberFormat);
System.log(numberFormat + ":  " + numberAsString);

numberFormat = "###";
numberAsString = System.formatNumber(myNumber, numberFormat);
System.log(numberFormat + ":  " + numberAsString);

numberFormat = "####";
numberAsString = System.formatNumber(myNumber, numberFormat);
System.log(numberFormat + ":  " + numberAsString);

numberFormat = "#####";
numberAsString = System.formatNumber(myNumber, numberFormat);
System.log(numberFormat + ":  " + numberAsString);
