workflow-item.script:
var i;
var randomNumber;
const maxNumber = 3;
var total = 0;
const numIterations = 1000000;

for (i=1 ; i<=numIterations ; i++) {
    randomNumber = Math.random() * maxNumber;
    //System.log("random=" + randomNumber);
    //System.log("floor=" + Math.floor(randomNumber));
    //System.log("ceil=" + Math.ceil(randomNumber));
    //System.log("round=" + Math.round(randomNumber));
    if (Math.ceil(randomNumber) < 1) { System.log("LOW"); }
    if (Math.ceil(randomNumber) > maxNumber) { System.log("HIGH"); }
    total += Math.ceil(randomNumber);
    //System.log("========================");
}

System.log("average=" + (total / numIterations));
